mapper.select:
select seq ,title ,content ,writer ,date_format(write_date,'%y.%m.%d') write_date ,date_format(write_date,'%y.%m.%d %H시%i분') write_date2 ,rec_cnt ,nrec_cnt from free where 1 = 1
and seq = #{seq}
and date_format(write_date,'%Y%m%d') >= #{fWrite}
and date_format(write_date,'%Y%m%d') <= #{tWrite}
and title like concat('%',#{title},'%')
and del_yn = 'N' order by seq desc limit #{limit} offset #{offset}

mapper.insert:
insert into free
(
	title
	, content
	, writer
) values (
	#{title}
	,#{content}
	,#{writer}
)

mapper.delete:
update free set
	del_yn = 'Y'
where seq = #{seq}

mapper.update:
		update free set
			title = #{title}
    		,content = #{content}
    		,update_date = current_timestamp
		where seq = #{seq}
